
document.addEventListener("DOMContentLoaded", function() {
    const testimonials = document.querySelectorAll('.testimonial');
    let currentTestimonial = 0;
    const testimonialSlider = document.querySelector('.testimonial-slider');
    const pauseButton = document.createElement('button');
    pauseButton.innerHTML = 'Pause';
    pauseButton.classList.add('pause-btn');
    document.body.appendChild(pauseButton);

    function showNextTestimonial() {
        testimonials[currentTestimonial].classList.remove('active');
        currentTestimonial = (currentTestimonial + 1) % testimonials.length;
        testimonialSlider.style.transform = `translateX(-${currentTestimonial * 100}%)`;
        testimonials[currentTestimonial].classList.add('active');
    }

    let autoPlay = setInterval(showNextTestimonial, 3000);

    pauseButton.addEventListener('click', function() {
        if (pauseButton.innerHTML === 'Pause') {
            clearInterval(autoPlay);
            pauseButton.innerHTML = 'Play';
        } else {
            autoPlay = setInterval(showNextTestimonial, 3000);
            pauseButton.innerHTML = 'Pause';
        }
    });

    // Scroll animations for generation sections
    const sections = document.querySelectorAll('.generation-section');
    window.addEventListener('scroll', function() {
        sections.forEach(section => {
            const sectionTop = section.getBoundingClientRect().top;
            const triggerPoint = window.innerHeight - 100;
            if (sectionTop < triggerPoint) {
                section.classList.add('show');
            }
        });
    });
});
